import React, { useState, useEffect } from "react";

function SearchForm({ onSearch }) {
  const [keyword, setKeyword] = useState("");
  const [source, setSource] = useState("");
  const [spatialResolution, setSpatialResolution] = useState("");
  const [temporalResolution, setTemporalResolution] = useState("");

  useEffect(() => {
    onSearch({ keyword, source, spatial_resolution: spatialResolution, temporal_resolution: temporalResolution });
  }, [keyword, source, spatialResolution, temporalResolution, onSearch]);

  return (
    <form className="search-form">
      <input
        type="text"
        placeholder="Search by keyword"
        value={keyword}
        onChange={(e) => setKeyword(e.target.value)}
      />
      <input
        type="text"
        placeholder="Filter by source"
        value={source}
        onChange={(e) => setSource(e.target.value)}
      />
      <input
        type="text"
        placeholder="Filter by spatial resolution"
        value={spatialResolution}
        onChange={(e) => setSpatialResolution(e.target.value)}
      />
      <input
        type="text"
        placeholder="Filter by temporal resolution"
        value={temporalResolution}
        onChange={(e) => setTemporalResolution(e.target.value)}
      />
    </form>
  );
}

export default SearchForm;
