import React, { useState, useEffect } from "react";
import axios from "axios";
import SearchForm from "./components/SearchForm";
import DatasetList from "./components/DatasetList";
import "./App.css";

function App() {
  const [datasets, setDatasets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch all datasets on initial load
  useEffect(() => {
    fetchDatasets();
  }, []);

  // Function to fetch datasets
  const fetchDatasets = async (params = {}) => {
    try {
      setLoading(true);
      const response = await axios.get("http://127.0.0.1:8000/datasets/search", {
        params,
      });
      setDatasets(response.data);
      setError(null);
    } catch (err) {
      setError("Failed to fetch datasets. Please try again.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // Handle search form submission
  const handleSearch = (filters) => {
    fetchDatasets(filters);
  };

  return (
    <div className="App">
      <h1>Data Catalog</h1>
      <SearchForm onSearch={handleSearch} />
      {loading && <p>Loading datasets...</p>}
      {error && <p className="error">{error}</p>}
      <DatasetList datasets={datasets} />
    </div>
  );
}

export default App;
