import React from "react";
import { FaFileCsv, FaFileExcel, FaFileCode, FaExternalLinkAlt } from "react-icons/fa";

function DatasetList({ datasets }) {
  if (datasets.length === 0) {
    return <p>No datasets found.</p>;
  }

  const getFormatIcon = (source) => {
    if (source.includes("CSV")) return <FaFileCsv className="format-icon" />;
    if (source.includes("Excel")) return <FaFileExcel className="format-icon" />;
    if (source.includes("JSON")) return <FaFileCode className="format-icon" />;
    return null;
  };

  return (
    <div className="dataset-list">
      {datasets.map((dataset) => (
        <div key={dataset.id} className="dataset">
          <h3>
            {getFormatIcon(dataset.source)} {dataset.title}
            {dataset.url && (
              <a href={dataset.url} target="_blank" rel="noopener noreferrer" className="external-link">
                <FaExternalLinkAlt />
              </a>
            )}
          </h3>
          <p><strong>Source:</strong> {dataset.source}</p>
          <p><strong>Spatial Resolution:</strong> {dataset.spatial_resolution}</p>
          <p><strong>Temporal Resolution:</strong> {dataset.temporal_resolution}</p>
          <p><strong>Methodology:</strong> {dataset.methodology}</p>
          <p><strong>Limitations:</strong> {dataset.limitations}</p>
          <p><strong>Advantages:</strong> {dataset.advantages}</p>
          <p><strong>Disadvantages:</strong> {dataset.disadvantages}</p>
        </div>
      ))}
    </div>
  );
}

export default DatasetList;
