import React, { useState, useEffect } from "react";

function SearchForm({ onSearch }) {
  const [keyword, setKeyword] = useState("");
  const [source, setSource] = useState("");
  const [spatialResolution, setSpatialResolution] = useState("");
  const [temporalResolution, setTemporalResolution] = useState("");

  // Sample options for dropdowns (replace with actual data from the backend if needed)
  const sourceOptions = ["CRM System", "ERP System", "NASA", "WHO"];
  const spatialResolutionOptions = ["High", "Medium", "Low"];
  const temporalResolutionOptions = ["Daily", "Monthly", "Yearly"];

  useEffect(() => {
    onSearch({ keyword, source, spatial_resolution: spatialResolution, temporal_resolution: temporalResolution });
  }, [keyword, source, spatialResolution, temporalResolution, onSearch]);

  return (
    <form className="search-form">
      <input
        type="text"
        placeholder="Search by keyword"
        value={keyword}
        onChange={(e) => setKeyword(e.target.value)}
      />
      <select value={source} onChange={(e) => setSource(e.target.value)}>
        <option value="">Filter by source</option>
        {sourceOptions.map((option, index) => (
          <option key={index} value={option}>
            {option}
          </option>
        ))}
      </select>
      <select value={spatialResolution} onChange={(e) => setSpatialResolution(e.target.value)}>
        <option value="">Filter by spatial resolution</option>
        {spatialResolutionOptions.map((option, index) => (
          <option key={index} value={option}>
            {option}
          </option>
        ))}
      </select>
      <select value={temporalResolution} onChange={(e) => setTemporalResolution(e.target.value)}>
        <option value="">Filter by temporal resolution</option>
        {temporalResolutionOptions.map((option, index) => (
          <option key={index} value={option}>
            {option}
          </option>
        ))}
      </select>
    </form>
  );
}

export default SearchForm;
