import React, { useState, useEffect } from "react";

function SearchForm({ onSearch }) {
  const [keyword, setKeyword] = useState("");
  const [source, setSource] = useState("");
  const [format, setFormat] = useState("");
  const [updateFrequency, setUpdateFrequency] = useState("");

  // Automatically trigger search when filters change
  useEffect(() => {
    onSearch({ keyword, source, format, update_frequency: updateFrequency });
  }, [keyword, source, format, updateFrequency, onSearch]);

  return (
    <form className="search-form">
      <input
        type="text"
        placeholder="Search by keyword"
        value={keyword}
        onChange={(e) => setKeyword(e.target.value)}
      />
      <select value={source} onChange={(e) => setSource(e.target.value)}>
        <option value="">Filter by source</option>
        <option value="CRM System">CRM System</option>
        <option value="ERP System">ERP System</option>
        <option value="NASA">NASA</option>
        <option value="WHO">WHO</option>
      </select>
      <select value={format} onChange={(e) => setFormat(e.target.value)}>
        <option value="">Filter by format</option>
        <option value="CSV">CSV</option>
        <option value="Excel">Excel</option>
        <option value="JSON">JSON</option>
      </select>
      <select
        value={updateFrequency}
        onChange={(e) => setUpdateFrequency(e.target.value)}
      >
        <option value="">Filter by update frequency</option>
        <option value="Monthly">Monthly</option>
        <option value="Quarterly">Quarterly</option>
        <option value="Yearly">Yearly</option>
      </select>
    </form>
  );
}

export default SearchForm;
